declare module 'unirest' {
    // Упрощенное объявление для unirest
    export interface UnirestRequest {
      headers(headers: Record<string, string>): this;
      send(body: string | object): this;
      then(): Promise<any>;
    }
    function unirest(method: string, url: string): UnirestRequest;
    export default unirest;
  }